import { useState } from "react";
import TodoList from "./components/TodoList";
import AddTodo from "./components/AddTodo";

export default function App() {
  const [todos, setTodos] = useState([
    { id: 1, text: "Clean my computer", completed: false },
    { id: 2, text: "Buy a keyboard", completed: false },
    { id: 3, text: "Write an article about @xstate/test", completed: true }
  ]);

  const [showAdd, setShowAdd] = useState(false);

  function addTodo(text) {
    if (!text.trim()) return;
    setTodos([...todos, { id: Date.now(), text, completed: false }]);
    setShowAdd(false);
  }

  function toggleTodo(id) {
    setTodos(todos.map(t =>
      t.id === id ? { ...t, completed: !t.completed } : t
    ));
  }

  return (
    <div className="p-10 max-w-3xl mx-auto">
      <h1 className="text-3xl font-bold mb-6 text-gray-900">
        Things to get done
      </h1>

      {showAdd ? (
        <AddTodo onSave={addTodo} onCancel={() => setShowAdd(false)} />
      ) : (
        <TodoList 
          todos={todos} 
          toggleTodo={toggleTodo} 
          showAdd={() => setShowAdd(true)} 
        />
      )}
    </div>
  );
}
